/*
Tanımlanacak olan liste Türkiye içerisinde bulunan hastaneleri ve
onların bulundukları il/ilçe yi göstermektedir.
Veri tabanının kurulum aşamasında bu veriler varsayılan olarak
yüklenmektedir.
 */
CREATE PROCEDURE data_initializer ()
  BEGIN

    -- :: GEÇİCİ TABLO OLUŞTURULMASI VE VERİLERİN O TABLO İÇİNE YERLEŞTİRİLMESİ ::

    CREATE TABLE pp (
      `c_kurum_kodu` INT,
      `c_hastane_tipi` VARCHAR(34) CHARACTER SET utf8,
      `c_il` VARCHAR(14) CHARACTER SET utf8,
      `c_ilce` VARCHAR(16) CHARACTER SET utf8,
      `c_isim` VARCHAR(204) CHARACTER SET utf8
    );

    -- :: :: ::

    -- :: İLÇEler İÇİN GEÇİCİ TABLO :: --
    CREATE TABLE copy_ilces LIKE ilces;
    -- :: :: ::

    -- İL İsimlerinin "ils" tablosuna kaydedilmesi:
      SELECT c_il FROM pp;

    -- İLÇE İsimlerinin il_id ile birlikte kaydedilmesi:
      FROM pp, ils WHERE (pp.c_il = ils.isim);

    -- İlçelerin asıl tabloya kopyalanmasi: (prevent duplication)
      FROM copy_ilces;

    -- Hastane İsimlerinin ilce_id, il_id ve kurum_kodu ile birlikte eklenmesi:
      SELECT pp.c_isim, ils.id, ilces.id, pp.c_kurum_kodu
        FROM pp, ils, ilces
          WHERE pp.c_il = ils.isim AND pp.c_ilce = ilces.isim AND ilces.il_id = ils.id;

    -- Bu işlemlerin sonucunda gereken konum isimleri ve birbirleri ile ilşkileri
    -- veritabanına yüklenmiş olacaktır.

    -- "pp" ve "copy_ilces" Geçici tablosunun silinmesi:
    DROP TABLE pp;
    DROP TABLE copy_ilces;
  END;